import ServiceCard from '../components/ServiceCard'
import { useLocation, useNavigate, useOutletContext } from 'react-router-dom'
import { useGetServicesQuery} from '../app/api/api'
import { CircularProgress, Slide } from "@mui/material";
import AddAddressPopup from '../components/AddAddressPopup';
import gsap from "gsap";
import { useGSAP } from "@gsap/react";
import { useEffect, useRef, useState } from 'react';
import { SetTitle } from '../components/SetTitle';
import { toast, ToastContainer } from 'react-toastify';

const Home = () => {
  const [addAddressPanel, setAddAddressPanel] = useState(false)
  const { user, token, isSuccess, isTokenLoading } = useOutletContext()

  const addAddressPopupRef = useRef()

  const { data: services, isLoading: isServicesLoading, isError } = useGetServicesQuery(undefined, {
    skip: !token
  })
  
  const navigate = useNavigate()
  const location = useLocation()

    useGSAP(() => {
      if (addAddressPanel) {
         gsap.to(addAddressPopupRef.current, {
          bottom: 80,
          transform: 'translateY(0)',
          delay: 0.3
        })
      }
      else {
        gsap.to(addAddressPopupRef.current, {
          bottom: 0,
          duration: 0.3,
          transform: 'translateY(100%)'
        })
      }
    }, [addAddressPanel])

    useEffect(()=> {
      if (user && !user?.user.address) {
        setAddAddressPanel(true)
      }
      else {
        setAddAddressPanel(false)
      }
    }, [token])

    useEffect(() => {
      
    }, [location])
    if (location.state?.showToast) {
      toast.success('You are logged out, see you soon!')
      setTimeout(() => {
        location.state.showToast = false
      }, 2000)
    }
  // static data
  const popularServices = [
    {
      id: 1,
      name: 'Electrician',
      image: 'data:image/jpeg;base64,',      
    },
    {
      id: 2,
      name: 'Plumber',
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 3,
      name: 'Carpenter',
      image: 'data:image/jpeg;base64,',
    },
    {
      id: 4,
      name: 'Painter',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_-V1iGBU3u6sluBoy1WyyXdCB48mk2DcAxg&s',
    },
    {
      id: 5,
      name: 'Mechanic',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSiXHQiq-xdR1ngjsKLdc6YiWl9-wn7bVhAEA&s',
    }
  ]
  return (
    <div className='w-full h-screen font-montserrat py-5 px-4 relative'>
        <div className='flex items-center justify-between w-full'>
        <h2 className='text-lg font-bold w-full text-center ml-6'>Services</h2>
        {
          token ? <i className="text-2xl plas ri-shopping-cart-2-line"></i> : <i onClick={()=> navigate('/user/login') } className="text-2xl plas ri-user-3-line"></i>
        }
        </div>
        <div className='mt-8 relative'>
        <i className="text-gray-600 text-2xl absolute top-[18%] left-4 ri-search-line"></i>
            <input className='w-full py-3 px-4 pl-12 placeholder:text-base placeholder:text-gray-500 rounded-lg bg-[#E8EEF2]' type="search" placeholder='Search for service' />
        </div>
        <div className='mt-6 w-full'>
          {
            isServicesLoading && (
              <div className='flex items-center justify-center pt-10'><CircularProgress/></div>
            )
          }
          {
            isSuccess ? (
          <h1 className='text-2xl font-bold'>{services?.uniqueServiceType.length > 0 ? 'All Services' : 'Popular Services'}</h1>,
              <div className='mt-6 space-y-7 pb-24'>
            {
              services?.uniqueServiceType.map((service, key) => (
                <ServiceCard key={key} serviceType={service}/>
              ))
            }
          </div>
            ) : !token && !isTokenLoading && (
              <div className='mt-5 space-y-7 pb-24'>
                <p className='text-xs text-gray-600'><b><i className="ri-information-line"></i> Note: </b>This is a sample list of services ,for testing purpose only. You can add your own services or login for actual services from backend </p>
                {
                  popularServices.map((service, key) => (
                    <ServiceCard key={key} serviceType={service.name} image={service.image}/>
                  ))
                }
              </div>
            )
          }
          {
            services?.uniqueServiceType.length === 0 && (
              <h4 className='text-base text-gray-600 font-semibold text-center leading-5'>No services found in your location</h4>
            )
          }
        </div>
        <div ref={addAddressPopupRef} className='fixed z-10 left-0 right-0 bottom-0 translate-y-full h-fit w-full'>
        <AddAddressPopup setAddAddressPanel={setAddAddressPanel}/>
        </div>
        <SetTitle title='Home'/>
    </div>
  )
}

export default Home